import { Component, OnInit, Inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { Item } from 'src/app/models/Item';
import { ItemService } from 'src/app/services/item/item.service';

@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.css'],
})
export class TodoItemComponent implements OnInit {
  itemForm: FormGroup = new FormGroup([]);
  constructor(
    protected formBuilder: FormBuilder,
    public dialogRef: MatDialogRef<TodoItemComponent>,
    @Inject(MAT_DIALOG_DATA) public data: { id: number },
    private service: ItemService
  ) {}
  ngOnInit(): void {
    if (this.data.id) {
      this.service.getById(this.data.id).subscribe((item) => {
        if (item) {
          this.initializeForm(item);
        }
      });
    }
    this.initializeForm();
  }
  initializeForm(item?: Item) {
    if (item) {
      this.itemForm = this.formBuilder.group({
        id: [item.id],
        description: [item.description, Validators.required],
        dueDate: [item.dueDate, [Validators.required]],
        priority: [item.priority, Validators.required],
      });
    } else {
      this.itemForm = this.formBuilder.group({
        id: [],
        description: [null, Validators.required],
        dueDate: [null, [Validators.required]],
        priority: [null, Validators.required],
      });
    }
  }
  onSubmit() {
    const vf = this.itemForm.value;
    if (vf.id) {
      this.service.create(this.itemForm.value).subscribe((res) => {
        this.onClose(res);
      });
    } else {
      this.service.create(this.itemForm.value).subscribe((res) => {
        this.onClose(res);
      });
    }
  }

  onClose(item?: Item) {
    this.dialogRef.close(item);
  }
}
<mat-card fxLayout="column" fxLayoutAlign="space-between start">
  <mat-card-title> {{ data.id ? "Edit " : "Add a new" }} task</mat-card-title>
  <mat-divider></mat-divider>
  <mat-card-content>
    <form [formGroup]="itemForm" fxFlex>
      <div fxLayout="column">
        <div fxLayout="row" fxLayoutAlign="start">
          <mat-form-field>
            <mat-label>Due date</mat-label>
            <input
              formControlName="dueDate"
              matNativeControl
              fxFlex="80"
              type="date"
            />
          </mat-form-field>
          <mat-form-field>
            <mat-label>Priority Level</mat-label>
            <input
              formControlName="priority"
              matNativeControl
              fxFlex="20"
              type="number"
            />
          </mat-form-field>
        </div>
        <div fxLayout="row" fxLayoutAlign="space-between start">
          <mat-form-field fxFlex="100">
            <mat-label>Description</mat-label>
            <textarea formControlName="description" matInput></textarea>
          </mat-form-field>
        </div>
      </div>
      <div fxFlexAlign="end">
        <button
          mat-raised-button
          (click)="onSubmit()"
          [disabled]="itemForm.invalid"
          color="accent"
        >
          {{ data.id ? "Save" : "Add" }}
        </button>
        <button (click)="onClose()" mat-raised-button>Cancel</button>
      </div>
    </form>
  </mat-card-content>
</mat-card>
